<?php		namespace Dez;    include_once __DIR__ .'/Constants.php';	class Autoloader {        private static            $includeDirs = array();		public function __construct(){            static::addIncludeDirs( __DIR__ );			spl_autoload_register( array( $this, 'loadClass' ) );		}        public static function addIncludeDirs() {            $args = func_get_args();            if( ! empty( $args ) ) {                foreach( $args as $dir ) {                    static::$includeDirs[] = $dir;                }            }        }		public function loadClass( $className = null ){            $parts  = explode( '\\', $className );            $scope  = array_shift( $parts );            $file   = join( DS, $parts ) .'.php';            foreach( static::$includeDirs as $dir ) {                if( file_exists( $dir . DS . $file ) ) {                    include_once $dir . DS . $file; return true;                }            }		}	}