<?php	namespace Dez\Core;    use Dez\Error\Exception;    /**     * @property \Dez\Core\Config           $config     * @property \Dez\Dispatcher\Dispatcher $dispatcher     * @property \Dez\Core\Session          $session     * @property \Dez\Core\Auth             $auth     * @property \Dez\Error\Error           $error     * @property \Dez\Core\Request          $request     * @property \Dez\Core\Router           $router     * @property \Dez\Core\Action           $action     * @property \Dez\ORM\Connection\DBO    $db     * @property \Dez\View\View             $view     * @property \Dez\Web\Layout            $layout     * @property \Dez\Response\Response     $response     */	abstract class App {        use ObjectTrait;        static protected            $appStorage     = [];        public function __construct() {            // system            \Dez::setAlias( '@dez-root',     realpath( __DIR__ .'/..' ) );            \Dez::setAlias( '@cache',        CACHE_DIR );            \Dez::setAlias( '@app',          APP_PATH );            \Dez::setAlias( '@modules',      '@app/modules' );            // web            \Dez::setAlias( '@host',         Url::host() );            \Dez::setAlias( '@full_web',     rtrim( Url::base(), '/' ) );            \Dez::setAlias( '@web',          rtrim( Url::web(), '/' ) );            \Dez::setAlias( '@media',        '@web/media' );            \Dez::setAlias( '@js',           '@media/js' );            \Dez::setAlias( '@css',          '@media/css' );        }		public function __get( $name ){			if( isset( static::$appStorage[$name] ) ) {				return static::$appStorage[$name];			} else {                throw new Exception\InvalidCall( $this->getClassName() .' [Getting undefined property]' );			}		}		public function attach( $name, $value ) {			static::$appStorage[$name] = $value;		}		abstract public function run();	}