<?php		namespace Dez\Core;		class Config {				private $_config;        public function __construct( $config_file ){			$this->_config = parse_ini_file( $config_file, true );		}        public function get( $key = null, $default = null ){			if( $key == null ) return $this->_config;			if( isset( $this->_config[$key] ) ){				return $this->_config[$key];			}else{				return $default;			}		}        public function path( $path, $default = null ) {            $parts  = array_filter( explode( '.', trim( $path ) ), 'strlen' );            if( empty( $parts ) ) {                return $default;            }            $data   = $this->_config;            foreach( $parts as $part ) {                if( isset( $data[$part] ) ) {                    $data = &$data[$part];                } else {                    $data = $default;                    break;                }            }            return $data;        }	}