<?php	namespace Sy\Core;    use Sy\Error\Error;	abstract class App {		public			$environment;		protected			$_objects = array();		public			$config = array();        public function __construct() {            \Sy::setAlias( '@appPath', APP_PATH );            \Sy::setAlias( '@host', trim( URI::base( true ), '/' ) );            \Sy::setAlias( '@web', trim( URI::base( false ), '/' ) );        }		public function __get( $name ){			if( isset( $this->_objects[$name] ) ){				return $this->_objects[$name];			}else{                Error::critical( 'Trying to get undefined property "'. $name .'" of '. __CLASS__ . ' object' );				return new \stdClass();			}		}		public function attachObject( $name, $object ){			if( ! is_object( $object ) ){				return false;			}			$this->_objects[$name] = $object;		}		abstract public function run();	}