<?php	namespace Sy\Core;    use Sy\Error\Exception;	abstract class App {        use ObjectTrait;        static protected            $appStorage     = [];        public function __construct() {            \Sy::setAlias( '@host',     trim( URI::base( true ), '/' ) );        }		public function __get( $name ){			if( isset( static::$appStorage[$name] ) ) {				return static::$appStorage[$name];			} else {                throw new Exception\InvalidCall( $this->getClassName() .' [Getting undefined property]' );			}		}		public function attach( $name, $value ) {			static::$appStorage[$name] = $value;		}		abstract public function run();	}